import { useEffect, useRef, useState } from 'react';
import { CustomButton } from '../../shared/button/button';
import './beyondMatter.scss';
import { GradientBall } from '../../shared/gradientBall/gradientBall';
import { Fade } from 'react-awesome-reveal';
import { useSelector } from 'react-redux';
import { RootState } from '../../../store/store';

export function BeyondMatter() {
  const { language } = useSelector((state: RootState) => state.languageState);
  const buttonsSectionRef = useRef<HTMLDivElement>(null);
  const [buttonsWidth, setButtonsWidth] = useState<string | undefined>();

  useEffect(() => {
    if (buttonsSectionRef.current) {
      const width = buttonsSectionRef.current.offsetWidth;
      setButtonsWidth(`${width}px`);
    }
  }, [buttonsSectionRef.current]);

  const containerRef = useRef<HTMLDivElement>(null);
  const [animateLogo, setAnimateLogo] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        const [entry] = entries;
        if (entry.isIntersecting) {
          setAnimateLogo(true);
        }
      },
      { threshold: 0.5 }
    );

    if (containerRef.current) {
      observer.observe(containerRef.current);
    }

    return () => {
      if (containerRef.current) {
        observer.unobserve(containerRef.current);
      }
    };
  }, []);

  return (
    <div className="beyond-matter">
      <div className="gradient-ball-container-home">
        <GradientBall></GradientBall>
      </div>
      <div
        className={`beyond-matter-info-container ${
          animateLogo ? 'animate-logo' : ''
        }`}
        ref={containerRef}
      >
        <div className={`logo-container`}>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            xmlnsXlink="http://www.w3.org/1999/xlink"
            width="309"
            height="193"
            viewBox="0 0 309 193"
            fill="none"
          >
            <rect width="309" height="193" fill="url(#pattern0_47_40)" />
            <defs>
              <pattern
                id="pattern0_47_40"
                patternContentUnits="objectBoundingBox"
                width="1"
                height="1"
              >
                <use
                  xlinkHref="#image0_47_40"
                  transform="matrix(0.001 0 0 0.00160104 0 -0.000323834)"
                />
              </pattern>
              <image
                id="image0_47_40"
                width="1000"
                height="625"
                xlinkHref="data:image/png;base64,"
              />
            </defs>
          </svg>
        </div>
        <Fade
          direction="left"
          style={window.innerWidth > 650 ? { width: buttonsWidth } : {}}
          triggerOnce
        >
          {language === 'english' ? (
            <>
              <p>
                <span className="strong">Beyond Matter</span> is a pioneering
                European project redefining how we engage with art and cultural
                heritage in the digital era. Led by the{' '}
                <span className="strong">
                  ZKM | Center for Art and Media (Germany)
                </span>{' '}
                and the{' '}
                <span className="strong">Centre Pompidou (France),</span> it
                explores the impact of digitalization through virtual and
                interactive exhibitions, advanced technological tools, and
                innovative curatorial methodologies.
              </p>
              <p>
                The exhibition{' '}
                <span className="strong">
                  "Evoking the Ghosts of Modernity"
                </span>{' '}
                at the{' '}
                <span className="strong">Museum of Modern Art of Medellín</span>{' '}
                takes as its starting point the digital models of{' '}
                <span className="strong">Les Immatériaux and Iconoclash,</span>{' '}
                reinterpreting their discourses in the Colombian context.
              </p>
            </>
          ) : (
            <>
              <p>
                <span className="strong">Beyond Matter</span> es un proyecto
                europeo pionero que redefine la manera en que interactuamos con
                el arte y el patrimonio cultural en la era digital. Liderado por
                el{' '}
                <span className="strong">
                  ZKM | Center for Art and Media (Alemania)
                </span>{' '}
                y el <span className="strong">Centre Pompidou (Francia),</span>{' '}
                explora el impacto de la digitalización a través de exposiciones
                virtuales e interactivas, herramientas tecnológicas avanzadas y
                metodologías curatoriales innovadoras.
              </p>
              <p>
                La exposición{' '}
                <span className="strong">
                  "Evocando los fantasmas de la Modernidad"
                </span>{' '}
                en el{' '}
                <span className="strong">
                  Museo de Arte Moderno de Medellín
                </span>{' '}
                toma como punto de partida los modelos digitales de{' '}
                <span className="strong">Les Immatériaux y Iconoclash,</span>{' '}
                reinterpretando sus discursos en el contexto colombiano.
              </p>
            </>
          )}
        </Fade>
        <div style={{ width: 'max-content' }} ref={buttonsSectionRef}>
          <Fade direction="left" triggerOnce>
            <div className="buttons-section">
              <a
                href="https://beyondmatter.eu/about"
                target="_blank"
                rel="noopener noreferrer"
              >
                <CustomButton
                  title={language === 'english' ? 'KNOW MORE' : 'VER MÁS'}
                  color="#ffffff"
                  backgroundColor="#1c1c1c"
                ></CustomButton>
              </a>
              <a
                href="https://iconoclash.beyondmatter.eu/"
                target="_blank"
                rel="noopener noreferrer"
              >
                <CustomButton
                  title="MDE: ICONOCLASH"
                  color="#ffffff"
                  backgroundColor="#9e1c21"
                ></CustomButton>
              </a>
              <a
                href="https://lesimmateriaux.beyondmatter.eu/"
                target="_blank"
                rel="noopener noreferrer"
              >
                <CustomButton
                  title="MDE: LES IMMATERIAUX"
                  color="#ffffff"
                  backgroundColor="#9e1c21"
                ></CustomButton>
              </a>
            </div>
          </Fade>
        </div>
      </div>
    </div>
  );
}
